mapper.select:
SELECT * FROM ( SELECT TO_CHAR(s.record_date, 'YYYY-MM-DD') as record_date, s.stock_code, s.stock_name, s.stock_type, s.closing_price, s.opening_price, s.high_price, s.low_price, ROW_NUMBER() OVER (ORDER BY s.stock_code) as rn FROM STOCK_INFO s INNER JOIN ( SELECT stock_code, MAX(record_date) as max_date FROM STOCK_INFO GROUP BY stock_code ) latest ON s.stock_code = latest.stock_code AND s.record_date = latest.max_date WHERE 1=1
AND s.stock_code > #{lastId}
AND (LOWER(s.stock_name) LIKE LOWER('%' || #{search} || '%') OR LOWER(s.stock_code) LIKE LOWER('%' || #{search} || '%'))
ORDER BY s.stock_code ) WHERE rn <= #{limit}


SELECT * FROM (
    SELECT * FROM STOCK_INFO
    WHERE stock_code = #{stock_code}
    ORDER BY record_date DESC
) WHERE ROWNUM = 1

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM STOCK_INTEREST
WHERE USER_NUM = #{user_num,jdbcType=INTEGER}
AND STOCK_CODE = #{stock_code,jdbcType=VARCHAR}

mapper.delete:
DELETE FROM STOCK_INTEREST
WHERE USER_NUM = #{user_num,jdbcType=INTEGER}
AND STOCK_CODE = #{stock_code,jdbcType=VARCHAR}
